    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        //Boton registro
        String nombre = BotonRegistroNombre.getText();
        String telefono = BotonRegistroNumero.getText();
        //COPY INICIA AQUI

            Conexion conn = new Conexion();
            
            conn.establecerConexion();
            String sql = "INSERT INTO usuarios (user_handle, email, nombre, apellido, telefono) VALUES (?, ?, ?, ?, ?)";

         try (PreparedStatement pstmt = conn.prepareStatement(sql)) {
            // Crear un PreparedStatement a partir de la conexi칩n
            
            
            // Par치metros para la inserci칩n
            pstmt.setString(1, "valor_user_handle");
            pstmt.setString(2, "valor_email");
            pstmt.setString(3, "valor_nombre");
            pstmt.setString(4, "valor_apellido");
            pstmt.setString(5, "valor_telefono");
            
            // Ejecutar la inserci칩n
            int filasInsertadas = pstmt.executeUpdate();
            if (filasInsertadas > 0) {
                System.out.println("Se insertaron " + filasInsertadas + " filas correctamente.");
            } else {
                System.out.println("No se pudo insertar el registro.");
            }
        } catch (SQLException e) {
            System.out.println("Error al insertar el registro: " + e.getMessage());
        }
    }
        //TERMINA AQUI
